import React, { useContext } from 'react'
import '../App.css'
import dataContext from '../Contexts/dataContext'
import { dataProvider } from '../Contexts/dataContext'


export default function () {
    const { isWeightHeight, setWeightHeight, handleClicked, handleHeight, handleWeight } = useContext(dataContext);
    return (
        <>
            <dataProvider>

                {({ isWeightHeight, setWeightHeight, handleClicked, handleHeight, handleWeight }) =>
                    <div>
                        <div className="row">
                            <div id="inputData" className="col m6 s12" >
                                <label className="labelInput" >Weight (kg)</label>
                                <input onChange={(e) => handleHeight(e)} id="height" type="number" placeholder="50" min="1" />
                            </div>
                            <div id="inputData" className="col m6 s12">
                                <label className="labelInput" >Height (cm)</label>
                                <input id="weight" onChange={(e) => handleWeight(e)} type="number" placeholder="160" min="1" />
                            </div>
                        </div>
                        <div>
                            <button onClick={(e) => handleClicked(e)} className="submit">Calculator BMI</button>
                        </div>
                        <div>
                            {isWeightHeight.map((value) =>
                            (
                                ((value.height != null && value.weight != null) || (value.height !== 0 && value.weight !== 0)) ?

                                    < >
                                        {console.log(value)}
                                        {console.log(value.height)}
                                        {console.log(value.weight)}
                                        <p>{value.height}</p>
                                        <p>{value.weight}</p>
                                    </>
                                    :
                                    <>
                                        {/* {console.log(value)}
                                         {console.log(value.height)}
                                         {console.log(value.weight)} */}
                                    </>
                                // console.log(value)
                            ))
                            }
                        </div>
                        {console.log(isWeightHeight)}
                    </div>
                }
            </dataProvider>

        </>
    );
}
